import React,{useState} from 'react'

export default function Textbox(props) {
    const [text, changeText] = useState("Start typing here...");
    const [num, counter] = useState(wrodcounter)

    // funcion to count the number of words in the enterd text
    function WordCount() {
      // var text = document.getElementById("txt").value;
      let enText = text;
      let textsize = enText.length;
      let count = 0, i = 0, j = 0;
      while (i < textsize) {
          j = i;
          if (enText[j] === " " || enText[j] === "\n") {
              while (j < textsize && (enText[j] === " " || enText[j] === "\n")) {
                  j++;
              }
              i = j;
          }
          else {
              while (j < textsize && (enText[j] !== " " && enText[j] !== "\n")) {
                  j++;
              }
              i = j;
              count++;
          }
      }
      counter(count);
    }

    const changeHandler =(event)=>{
        
        changeText(event.target.value);
        WordCount();
      
    }


    const wrodcounter = ()=>{
        WordCount();
    }

  return (
    <div>
        <div className="container">
            <div className="mt-5">
                <label htmlFor="textbox" className="form-label">{props.heading}</label>
                <textarea   className="form-control" name="" id="textbox" cols="30" rows="10" value={text} onChange={changeHandler} onKeyUp={wrodcounter}></textarea>
            </div>
            <span>{props.outputmsg}</span><span id="msg">{num}</span>
        </div>
    </div>
  )
}
