import React,{useState} from 'react'

export default function Textbox(props) {
    const [text, changeText] = useState("Start typing here...");

    const changeHandler =(envent)=>{
      changeText(event.target.value);
    }


  return (
    <div>
        <div className="container">
            <div className="mt-5">
                <label htmlFor="textbox" className="form-label">{props.heading}</label>
                <textarea   className="form-control" name="" id="textbox" cols="30" rows="10" value={text} onchange={changeHandler}></textarea>
            </div>
            <span>{props.outputmsg}</span><span></span>
        </div>
    </div>
  )
}
